import React from 'react';
import logo from './logo.svg';
import './App.css';

class App extends React.Component{
  constructor(props) {
    super(props);
    this.state = {name: "React!", cond: false}
    this.handleChange = this.handleChange.bind(this);
    this.box = this.box.bind(this);

  }
  handleChange(e){
    this.setState({
      name: e.target.value
    });
  }
  box(e){
    let x = !(e.target.checked)
    console.log(x);
    this.setState({
      cond: x
    })
  }
  render(){
    return (
      <div>
        <h1>{this.state.name}!</h1>
        <input type="text" onChange={this.handleChange} />
        <hr />
        <input type="checkbox" checked={this.state.cond} onChange={this.box}/>
        <p>{this.state.cond}</p>
      </div>
    );
  }
}



export default App;
