import './App.css';

function App() {

  const [input, setInput] = useState('');
  let btns = [];
  let numbers = [];
  ['+', '-', '*', '/'].forEach((x) => {
    numbers.push(<button onClick={(e) => setInput(input + e.target.value)} value={x} key={x} className="btn">{x}</button>);
  })
  [ 1, 2, 3, 4, 5, 6, 7, 8 , 9, 0].forEach((x) =>{
     btns.push(<button onClick={(e) => setInput(input + e.target.value)} value={x} key={x} className="btn">{x}</button>);
   })
  return(
    <div className="container">
      <div className="upper-area">
        <h1>{input} </h1>
      </div>
      <div className='btns'>
        {numbers}
        {btns}
