import React, {useState, useEffect} from 'react';
import './App.css';

function App() {
  const [quote, setQuote] = useState(['Hello World', 'Hello World 2', 'Hello World 3', 'Hello World 3']);

  const [currentQuote, setCurrentQuote] = useState(Math.floor(Math.random() * 4));

  return(
    <div>
      <h1>Hello {quote[currentQuote]}</h1>
      <button onClick={() => setCurrentQuote(Math.floor(Math.random() * 4))}>Change</button>

    </div>
  )
}



export default App;
