import React, {useState} from 'react';
import './App.css';

function App() {

  const [input, setInput] = useState('00');
  let btns = [];
  [1, 2, 3, 4, 5, 6, 7, 8 , 9, 0, '+', '-', '*', '/'].forEach((x) =>{
     btns.push(<button onClick={(e) => setInput(input + e.target.value)} value={x} key={x} >{x}</button>);
   })
  return(
    <div className="container">
      <div className="upper-area">
        <h1>{input}</h1>
      </div>
      {btns}
      <button onClick={(e) => setInput(eval(input))} value="=" >=</button>


    </div>
  )
}



export default App;
